var Translator = require("..");
var TranslateAPI = new Translator().translate;
var args = process.argv[2] || null;
var text = process.argv[3] || null;
var src = process.argv[4] || null;
var tar = process.argv[5] || null;

switch (args) {
    case "callback":
        TranslateAPI(text, { src_lang: src, tar_lang: tar }, (err, returnText) => {
            if (err) return console.log(err);
            console.log(returnText);
        });
    break;
    case "promise":
        TranslateAPI(text, { src_lang: src, tar_lang: tar })
            .then(console.log)
            .catch(console.log);
    break;
    default:
        console.log(`npm run test <callback/promise> <text> <src_lang> <tar_lang>`)
    break;
}